mapper.select:
SELECT AC.*
FROM activity_member AS AM, account AS AC
WHERE AM.account_id = AC.account_id AND AM.activity_id = #{activityId};

mapper.update:
UPDATE activity_member AS A
SET A.read_status = #{readStatus}
WHERE A.activity_id = #{activityId} AND A.account_id != (
    SELECT AC.account_id
    FROM activity AS AC
    WHERE AC.activity_id = #{activityId}
);
